mapper.select:
SELECT a.operator_id, coalesce(b.action_date, #{action_time_start}, current_date) as action_time, a.job_no, a.name, pos_main.name as main_ability, group_concat(distinct pos_work.process_code separator ' ') as position_name, sum(worktime) as worktime FROM operator a left join ( SELECT position_id, operator_id, date(action_time) as action_date, COALESCE(sum(COALESCE(unix_timestamp(finish_time) - unix_timestamp(action_time),0)), 0) as worktime FROM production_feature WHERE action_time is not null
and action_time >= #{action_time_start}
and action_time < date_add(#{action_time_end}, interval 1 day)
group by position_id,operator_id,action_date
UNION ALL
SELECT position_id, operator_id, date(action_time) as action_date, COALESCE(sum(COALESCE(unix_timestamp(finish_time) - unix_timestamp(action_time),0)), 0) as worktime FROM solo_production_feature WHERE action_time is not null
and action_time >= #{action_time_start}
and action_time < date_add(#{action_time_end}, interval 1 day)
group by position_id,operator_id,action_date
) b on a.operator_id = b.operator_id left join position pos_main on a.position_id = pos_main.position_id left join position pos_work on b.position_id = pos_work.position_id
a.work_count_flg <> 0
and a.section_id = #{section_id}
and a.job_no = #{job_no}
and a.name like CONCAT('%','${name}','%')
and pos_main.line_id =#{line_id}
and a.delete_flg = 0
group by a.operator_id, b.action_date

select wkrslt.operator_id, wkrslt.action_time ,wkrslt.job_no ,wkrslt.name ,wkrslt.main_ability, pn.name as position_name, COALESCE(wt.worktime, 0) as worktime from ( SELECT DISTINCT a.operator_id, date(#{action_time_start}) as action_time, date_add(#{action_time_start}, interval 1 day) as action_time_end, a.job_no, a.name, c.name as main_ability FROM operator a left join production_feature b on a.operator_id = b.operator_id and (b.action_time is not null) and b.action_time >= #{action_time_start} and b.action_time < date_add(#{action_time_start}, interval 1 day) left join position c on a.position_id = c.position_id
a.work_count_flg <> 0
and a.job_no = #{job_no}
and a.name like CONCAT('%','${name}','%')
and a.section_id = #{section_id}
and a.position_id in (select position_id from position where line_id=#{line_id})
and a.delete_flg = 0
) wkrslt left join (select operator_id, sum(unix_timestamp(finish_time) - unix_timestamp(action_time)) as worktime from production_feature where finish_time is not null and action_time >= #{action_time_start} and action_time < date_add(#{action_time_start}, interval 1 day) group by operator_id ) wt ON wt.operator_id = wkrslt.operator_id left join (select operator_id, name from ( select p2.operator_id, p1.name, count(1) as cnt from position p1 join production_feature p2 ON p1.position_id = p2.position_id where p2.action_time >= #{action_time_start} and action_time < date_add(#{action_time_start}, interval 1 day) group by p2.operator_id, p1.name order by p2.operator_id, cnt desc) pn_in group by operator_id ) pn ON wkrslt.operator_id = pn.operator_id order by wkrslt.action_time;

mapper.delete:

DELETE FROM pause_feature
WHERE operator_id = #{operator_id}
AND pause_start_time = #{pause_start_time}
AND pause_finish_time is null
AND REASON BETWEEN 49 AND 70;


DELETE FROM pause_feature
WHERE operator_id = #{operator_id}
AND pause_start_time = #{pause_start_time}
AND reason in(40,41)

mapper.update:

UPDATE pause_feature
SET reason = #{reason}, 


pause_finish_time = #{pause_finish_time},


	comments = #{comments}
WHERE operator_id = #{operator_id}
AND pause_start_time = #{pause_start_time}
AND REASON BETWEEN 49 AND 70;


UPDATE pause_feature
SET reason = #{reason}, comments = #{comments},pause_start_time = #{pause_start_time},pause_finish_time=#{pause_finish_time}
WHERE operator_id = #{operator_id}
AND pause_start_time between #{action_time} and date_add(#{action_time}, interval 1 day)
AND reason in(40,41)

mapper.select:

SELECT operator_id
FROM pause_feature
WHERE operator_id = #{operator_id}
	AND pause_start_time = #{pause_start_time}
	AND alarm_messsage_id IS NULL
	AND REASON BETWEEN 49 AND 70;
