mapper.select:
SELECT device_jig_quotation.entrust_no, device_jig_quotation.quotation_no, device_jig_order.order_no, device_jig_order_detail.object_type, device_jig_order_detail.model_name, device_jig_order_detail.system_code, device_jig_order_detail.name, device_jig_order_detail.order_from, device_jig_order_detail.quantity, device_jig_invoice.order_price, device_jig_invoice.order_price * device_jig_order_detail.quantity AS total_order_price, device_jig_invoice.origin_price, (device_jig_invoice.origin_price - device_jig_invoice.order_price) * device_jig_order_detail.quantity AS differ_price, applicator_operator.name AS applicator_operator_name, device_jig_order_detail.nesssary_reason, device_jig_order_detail.applicate_date, device_jig_quotation.entrust_send_date, device_jig_invoice.send_date, device_jig_quotation.acquire_date, device_jig_quotation.delivery_osh_date, COALESCE(device_jig_order_detail.reorder_scheduled_date, device_jig_quotation.scheduled_date) AS scheduled_date, device_jig_order_detail.recept_date, device_jig_order_detail.confirm_flg, device_jig_order_detail.inline_recept_date, inline_receptor_operator.name AS inline_receptor_operator_name, device_jig_order_detail.budget_month, device_jig_order_detail.budget_description, device_jig_order_detail.order_key, device_jig_order_detail.device_type_id, device_jig_order_detail.applicator_id, device_jig_order_detail.confirm_quantity, device_jig_order_detail.quotation_id, device_jig_order_detail.invoice_no, device_jig_order_detail.invoice_date FROM device_jig_order_detail JOIN device_jig_order ON device_jig_order_detail.order_key = device_jig_order.order_key JOIN operator applicator_operator ON applicator_operator.operator_id = device_jig_order_detail.applicator_id LEFT JOIN operator inline_receptor_operator ON inline_receptor_operator.operator_id = device_jig_order_detail.inline_receptor_id LEFT JOIN device_jig_quotation ON device_jig_quotation.quotation_id = device_jig_order_detail.quotation_id LEFT JOIN device_jig_invoice ON device_jig_invoice.invoice_id = device_jig_order_detail.order_invoice_id
AND device_jig_quotation.quotation_no = #{quotation_no}
AND device_jig_order.order_no = #{order_no}
AND (device_jig_order_detail.model_name LIKE CONCAT('%',#{model_name},'%') OR device_jig_order_detail.system_code LIKE CONCAT('%',#{model_name},'%') OR device_jig_order_detail.name LIKE CONCAT('%',#{model_name},'%'))
AND device_jig_order_detail.applicator_id = #{applicator_id}
AND device_jig_order_detail.order_invoice_id IS NULL
AND device_jig_invoice.send_date >= #{send_date_start}
AND device_jig_invoice.send_date <= #{send_date_end}
AND device_jig_quotation.scheduled_date >= #{scheduled_date_start}
AND device_jig_quotation.scheduled_date <= #{scheduled_date_end}
AND device_jig_order_detail.recept_date >= #{recept_date_start}
AND device_jig_order_detail.recept_date <= #{recept_date_end}
AND device_jig_order_detail.inline_recept_date IS NULL
AND device_jig_order_detail.object_type in (1, 3)
AND device_jig_order_detail.object_type = 2
AND device_jig_order_detail.invoice_no LIKE CONCAT('%',#{invoice_no},'%')
AND device_jig_order_detail.order_from = #{order_from}

mapper.delete:

DELETE FROM device_jig_order_detail WHERE order_key = #{order_key} 
										AND object_type = #{object_type} 
										AND device_type_id = #{device_type_id}
										AND model_name = #{model_name}
										AND applicator_id = #{applicator_id}

mapper.update:

UPDATE 
	device_jig_order_detail
SET
	system_code = #{system_code},
	name = #{name},
	order_from = #{order_from},
	nesssary_reason = #{nesssary_reason}



	,quantity = #{quantity}



WHERE
