mapper.select:
SELECT device_jig_order.order_no, device_jig_order_detail.order_key, device_jig_order_detail.object_type, device_jig_order_detail.device_type_id, device_type.name AS device_type_name, device_jig_order_detail.model_name, device_jig_order_detail.system_code, device_jig_order_detail.name, device_jig_order_detail.order_from, device_jig_order_detail.quantity, device_jig_order_detail.order_invoice_id, device_jig_order_detail.applicator_id, applicator_operator.name AS applicator_operator_name, device_jig_order_detail.nesssary_reason, device_jig_order_detail.applicate_date, device_jig_order_detail.quotation_id, device_jig_order_detail.reorder_scheduled_date, device_jig_order_detail.recept_date, device_jig_order_detail.confirm_flg, device_jig_order_detail.confirm_quantity, device_jig_order_detail.inline_recept_date, device_jig_order_detail.inline_receptor_id, device_jig_order_detail.budget_month, device_jig_order_detail.budget_description, device_spare.available_inventory, device_jig_quotation.scheduled_date, device_jig_order_detail.invoice_no, device_jig_order_detail.invoice_date FROM device_jig_order_detail JOIN device_jig_order ON device_jig_order_detail.order_key = device_jig_order.order_key JOIN operator applicator_operator ON applicator_operator.operator_id = device_jig_order_detail.applicator_id LEFT JOIN device_type ON device_jig_order_detail.device_type_id = device_type.device_type_id LEFT JOIN device_spare ON device_spare.device_type_id = device_jig_order_detail.device_type_id AND device_spare.model_name = device_jig_order_detail.model_name AND device_spare.device_spare_type = 1 LEFT JOIN device_jig_quotation ON device_jig_quotation.quotation_id = device_jig_order_detail.quotation_id
AND device_jig_order_detail.order_key = #{order_key}
AND device_jig_order_detail.object_type = #{object_type}
AND device_jig_order_detail.device_type_id = #{device_type_id}
AND device_jig_order_detail.model_name = #{model_name}
AND device_jig_order_detail.quotation_id = #{quotation_id}

