mapper.select:
SELECT case_code FROM depose_storage WHERE material_id IS null
AND case_code like concat(LEFT(#{case_code}, 2) ,'%') AND case_code > #{case_code}
ORDER by case_code limit 1 ;

mapper.update:

UPDATE depose_storage
SET material_id = #{material_id},
refresh_time = current_timestamp
WHERE
case_code = #{case_code}
;


UPDATE depose_storage
SET material_id = null,
refresh_time = null
WHERE
material_id = #{material_id}
;
