mapper.select:
SELECT `m`.material_id, `m`.agreed_date, `m`.quotation_first, `m`.model_id, m.omr_notifi_no as sorc_no, `pf111`.finish_time as reception_time, `pf151`.finish_time as quotation_time, '' as symbol, `m`.material_id, `m`.direct_flg, `m`.service_repair_flg, `m`.fix_type, `m`.`level`, `m`.serial_no, `m`.selectable, `mdl`.name as model_name, `c`.`name` as customer_name, `m`.`ocm`, `m`.`ocm_rank`, `m`.`ocm_deliver_date`, `m`.`osh_deliver_date`, `srm`.countermeasures as storager, case when `m`.wip_location IS NULL THEN `ppf1`.operate_result ELSE '-1' END as operate_result, case when `m`.wip_location IS NULL THEN `ppf1`.name ELSE concat('WIP:', `m`.wip_location) END as processing_position,
as ticket_flg, `m`.scheduled_expedited, `ppf1`.position_id as category_id, `m`.bound_out_ocm, `m`.area, case when `mp`.material_id is null then -1 when `mp`.bo_contents is null then 0 else 1 end as partial_order_date FROM material m JOIN v_model mdl ON `m`.model_id = `mdl`.model_id LEFT JOIN production_feature pf111 ON `pf111`.material_id = `m`.material_id AND `pf111`.position_id = 9 AND `pf111`.operate_result = 2 LEFT JOIN production_feature pf151 ON `pf151`.material_id = `m`.material_id AND `pf151`.position_id IN (select position_id from position where special_page = 'quotation') AND `pf151`.operate_result = 2 JOIN (SELECT `pfp`.material_id, `pfp`.position_id, `pfp`.name, `pfp`.process_code, `pfp`.operate_result FROM ( SELECT `pf`.material_id, `pf`.position_id, `p`.name, `p`.process_code, `pf`.operate_result FROM production_feature pf JOIN `position` p ON `pf`.position_id = `p`.position_id AND `p`.line_id = 11 JOIN material pma ON pf.material_id = pma.material_id WHERE pma.inline_time is null AND pma.outline_time IS NULL AND pma.break_back_flg = 0 AND operate_result not in (5 , 8) ORDER BY (1-ISNULL(`pf`.action_time)) , `pf`.action_time DESC, `pf`.operate_result DESC ) pfp GROUP BY `pfp`.material_id) ppf1 ON `m`.material_id = `ppf1`.material_id LEFT JOIN (select * from service_repair_manage where material_id like '0%') `srm` ON cast(`m`.material_id as char) = `srm`.material_id LEFT JOIN customer `c` ON `m`.customer_id = `c`.customer_id LEFT JOIN material_partial mp ON `m`.material_id = `mp`.material_id AND `mp`.occur_times = 1 WHERE `m`.inline_time IS NULL AND outline_time IS NULL AND break_back_flg = 0
AND `m`.omr_notifi_no LIKE CONCAT('%', #{sorc_no}, '%')
AND `m`.model_id = #{model_id}
AND `m`.serial_no = #{serial_no}
AND `m`.level = #{level}
AND `m`.agreed_date IS NULL
AND `m`.agreed_date IS NOT NULL
AND `m`.wip_location LIKE CONCAT('%', #{wip_location}, '%')
ORDER BY `m`.scheduled_expedited DESC, reception_time, `ppf1`.PROCESS_CODE;
