mapper.select:
SELECT pf.material_id, pf.position_id, pf.section_id, pf.pace, pf.operator_id, pf.operate_result, pf.action_time, pf.finish_time, pf.pcs_inputs, pf.pcs_comments, pf.rework FROM production_feature pf JOIN material m ON pf.material_id = m.material_id WHERE pf.position_id = #{position_id}
and pf.section_id = #{section_id}
and pf.material_id = #{material_id}
and pf.action_time is null and pf.finish_time is null
and m.quotation_first = 0
and m.quotation_first = 1


SELECT Count(material_id) FROM production_feature
WHERE position_id = #{position_id}
and section_id = #{section_id}
and action_time is null
and finish_time is null;

SELECT `m`.material_id, `m`.omr_notifi_no as sorc_no, `m`.model_id, `mdl`.name as model_name, `c`.name as category_name, `m`.serial_no, (`m`.scheduled_expedited * 10 + COALESCE(`mp`.line_expedited,0)) as expedited, `pf`.operate_result as waitingat, coalesce(`psf`.reason, 30) as pause_reason, coalesce(`mp`.scheduled_assign_date = current_date, 0) as today, (`mp`.rework_trigger_position_id is not null) as reworked, coalesce(`m`.direct_flg, 0) as direct_flg, m.level in (9,91,92,93,96,97,98) as light_fix, m.level, CASE WHEN break_back_flg > 0 THEN break_back_flg WHEN EXISTS (select 1 from `for_solution_area` where material_id = m.material_id and (position_id = pf.position_id or position_id is null) and solved_time IS NULL AND NOT (reason = 2 AND date(`happen_time`) = date(current_timestamp) AND f_diffworktime(`happen_time`, coalesce(solved_time, current_timestamp)) < 0.06)) THEN 2 ELSE 0 END as block_status, CASE WHEN operate_result = 7 THEN (SELECT shelf_name FROM depose_storage WHERE material_id = `m`.material_id) ELSE null END as shelf_name FROM production_feature pf JOIN material m ON `pf`.material_id = `m`.material_id JOIN model mdl ON `m`.model_id = `mdl`.model_id JOIN category c ON `mdl`.category_id = `c`.category_id LEFT JOIN material_process mp ON `mp`.line_id = #{line_id} AND `mp`.material_id = `m`.material_id LEFT JOIN (select material_id, SECTION_ID, POSITION_ID, reason from pause_feature where PAUSE_FINISH_TIME IS NULL) psf ON `pf`.material_id = `psf`.material_id AND `psf`.SECTION_ID = `pf`.section_id AND `psf`.POSITION_ID = `pf`.position_id LEFT JOIN inline_plan ip ON `pf`.material_id = `ip`.material_id WHERE `pf`.position_id = #{position_id}
AND `pf`.section_id = #{section_id}
AND `pf`.action_time is null AND `pf`.finish_time is null AND `pf`.operate_result not in (5, 8) AND (`pf`.operator_id = #{operator_id} OR `pf`.operate_result <> 3)
and m.quotation_first = 0
and m.quotation_first = 1
ORDER BY shelf_name is not null, expedited desc, m.scheduled_date, ip.seq, serial_no;


SELECT pf.reason, count(pf.reason) as count_reason
FROM pause_feature pf
JOIN material m
	ON pf.material_id = m.material_id
WHERE pf.pause_start_time > current_date
and pf.position_id = #{position_id}
and pf.section_id = #{section_id}



and m.quotation_first = 0




and m.quotation_first = 1



GROUP BY pf.reason;


SELECT `pf`.material_id, `pf`.position_id, `pf`.section_id, `pf`.pace, `pf`.operator_id, `pf`.operate_result,
	`pf`.action_time, `pf`.finish_time, `pf`.pcs_inputs, `pf`.pcs_comments, `pf`.rework, `s`.name as section_name
FROM production_feature pf
LEFT JOIN section s
	ON `pf`.section_id = `s`.section_id
WHERE `pf`.position_id = #{position_id}
AND `pf`.material_id = #{material_id}
AND `pf`.action_time IS NOT NULL
ORDER BY action_time;


SELECT COALESCE(sum(unix_timestamp(COALESCE(finish_time, current_timestamp)) - unix_timestamp(action_time)), 0) FROM production_feature
WHERE position_id = #{position_id}
and section_id = #{section_id}
and material_id = #{material_id}
and not (operate_result = 4 and finish_time is null)
and rework = #{rework};


SELECT material_id, position_id, section_id, pace, operator_id, operate_result,
	`action_time, finish_time, pcs_inputs, pcs_comments, rework
FROM production_feature
WHERE operator_id = #{operator_id}
ORDER BY COALESCE(action_time, "9999/12/31")
LIMIT 1;


select avg(sum_seconds) / 60 from (
select pf.material_id, sum(use_seconds) as sum_seconds from material_process mp
join production_feature pf
on mp.material_id = pf.material_id
where mp.line_id = #{line_id}
and pf.use_seconds is not null
and pf.position_id in (select position_id from position where line_id = #{line_id}
and process_code not in ('301', '302')
)
group by pf.material_id
order by mp.finish_date desc, mp.material_id desc
limit 10
) sample;
