SELECT ma.material_id, ma.serial_no, ma.model_id, mdl.name as model_name, inline_time, outline_time, ticket_flg FROM material ma JOIN v_model mdl ON ma.model_id = mdl.MODEL_ID WHERE fix_type in (6, 7, 8) AND serial_no in
#{serial_no}
AND (outline_time IS NULL OR outline_time > date_add(current_date, interval -1 year)) ORDER by serial_no asc
