mapper.select:
select group_concat(cfm_d.check_manage_code order by cfm_d.check_manage_code separator ' ') as daily_sheet_manage_no, group_concat(cfm_r.check_manage_code order by cfm_r.check_manage_code separator ' ') as regular_sheet_manage_no, ot.name as responsible_operator, l.name as line_name, s.name as section_name, p.process_code, dt.name, dm.devices_manage_id, dm.manage_code, dm.measuring_instrument_manage_code, dm.device_type_id, dm.model_name, dm.manager_operator_id, o.name as manager, dm.manage_level, dm.asset_no, dm.products_code, dm.brand_id, b.name as brand_name, dm.section_id, dm.line_id, dm.position_id, dm.responsible_operator_id, dm.import_date, dm.provide_date, dm.waste_date, dm.delete_flg, op.name as updated_by, dm.updated_time, dm.status, dm.comment, dm.location from devices_manage dm join device_type dt ON dt.device_type_id = dm.device_type_id left join OPERATOR O ON dm.manager_operator_id = O.OPERATOR_ID join OPERATOR op ON dm.updated_by = op.OPERATOR_ID left join operator ot ON dm.responsible_operator_id = ot.OPERATOR_ID left join position p ON dm.position_id = p.position_id left join section s ON dm.section_id = s.section_id left join line l ON dm.line_id = l.line_id left join check_file_manage cfm_d ON dm.device_type_id = cfm_d.device_type_id and cfm_d.delete_flg != 1 AND ((dm.status = 1 AND cfm_d.access_place = 1) OR (dm.status = 5 AND cfm_d.access_place = 9)) AND (cfm_d.specified_model_name IS NULL OR cfm_d.specified_model_name regexp CONCAT('(^|;)', dm.model_name, '($|;)')) left join check_file_manage cfm_r ON dm.device_type_id = cfm_r.device_type_id AND cfm_r.access_place = 2 AND (dm.status = 1) and cfm_r.delete_flg != 1 AND (cfm_r.specified_model_name IS NULL OR cfm_r.specified_model_name regexp CONCAT('(^|;)', dm.model_name, '($|;)'))
left join brand b ON dm.brand_id = b.brand_id
dm.delete_flg=0
and dm.status in(${status})
and dm.manage_code LIKE CONCAT('%',#{manage_code},'%')
and dt.device_type_id =#{device_type_id}
and dt.specialized =#{specialized}
and dm.brand_id =#{brand_id}
and dm.model_name LIKE CONCAT('%',#{model_name},'%')
and dm.section_id =#{section_id}
and dm.line_id =#{line_id}
and dm.manage_level = #{manage_level}
and (dm.asset_no = #{asset_no} OR dm.measuring_instrument_manage_code = #{asset_no})
and dm.manager_operator_id = #{manager_operator_id}
and dm.position_id =#{position_id}
group by devices_manage_id ORDER BY
dm.manage_code,
updated_time DESC

SELECT dm.manage_code, dt.name, dm.model_name, sec.name AS section_name, line.name AS line_name, pos.process_code, manager_oper.name AS manager, dm.provide_date, provide_oper.name AS provider, dm.manage_level, dm.location, dm.products_code, dm.brand_id, b.name as brand_name, dm.import_date, dm.status, dm.comment FROM devices_manage dm JOIN device_type dt ON dm.device_type_id=dt.device_type_id LEFT JOIN section sec ON sec.section_id=dm.section_id LEFT JOIN line ON line.line_id=dm.line_id LEFT JOIN position pos ON pos.position_id=dm.position_id LEFT JOIN operator manager_oper ON manager_oper.operator_id=dm.manager_operator_id LEFT JOIN operator provide_oper ON provide_oper.operator_id=dm.updated_by LEFT JOIN brand b ON dm.brand_id = b.brand_id WHERE dm.delete_flg=0 AND dm.status IN(1,4)
AND dm.manage_code LIKE CONCAT('%',#{manage_code},'%')
AND dt.device_type_id=#{device_type_id}
AND dm.model_name LIKE CONCAT('%',#{model_name},'%')
AND dm.section_id=#{section_id}
AND dm.line_id=#{line_id}
AND dm.position_id=#{position_id}
AND dm.manager_operator_id=#{manager_operator_id}
AND dm.provide_date >= #{provide_date_start}
AND dm.provide_date <= #{provide_date_end}
AND dm.manage_level=#{manage_level}
order by dm.status, dm.position_id, dm.manage_code


  SELECT manage_code FROM devices_manage
  WHERE manage_code= #{manage_code} and delete_flg = 0



	and devices_manage_id <> #{devices_manage_id}

SELECT dm.devices_manage_id, dm.manage_code, exists( SELECT manage_id From check_unqualified_record WHERE manage_id = dm.devices_manage_id and object_type = 1 and (position_handle = 3 or position_handle is null) and (product_content != 1 or position_handle is null)) as check_result FROM devices_manage dm WHERE dm.delete_flg = 0 and dm.status = 5 and dm.device_type_id = #{device_type_id}
and dm.model_name = #{model_name}
ORDER BY dm.manage_code
