mapper.select:
SELECT LINE_ID, NAME, INLINE_FLG, IN_ADVANCE, UPDATED_BY, UPDATED_TIME FROM V_LINE
DELETE_FLG = 0
AND LINE_ID = #{line_id}
AND NAME LIKE CONCAT('%', #{name}, '%')
AND INLINE_FLG = #{inline_flg}
AND EXISTS (select p.process_code from position p join position_of_section p_s on p.position_id = p_s.position_id join section s on p_s.section_id = s.section_id where p.delete_flg = 0 and p.line_id = V_LINE.line_id and s.department = #{department})
ORDER BY
LINE_ID,
UPDATED_TIME DESC;
