mapper.select:
SELECT fact_pf_key, production_type, operator_id, action_time, finish_time, partial_warehouse_key, material_id, occur_times FROM fact_production_feature WHERE operator_id = #{operator_id}
AND partial_warehouse_key = #{partial_warehouse_key}
AND production_type = #{production_type}
AND material_id = #{material_id}

mapper.update:

UPDATE 
	fact_production_feature 
SET
	partial_warehouse_key = #{partial_warehouse_key}
WHERE
	fact_pf_key = #{fact_pf_key}
