mapper.update:

UPDATE
	partial_warehouse_detail
SET
	fact_pf_key = #{fact_pf_key},
	collation_quantity = #{collation_quantity}



	,quantity = #{quantity}



WHERE
	`key` = #{key} 
AND 
	partial_id = #{partial_id}

mapper.select:
SELECT v_partial.spec_kind, CASE WHEN pbs.box_count = -1 THEN SUM(1) ELSE CEIL(SUM(pwd.quantity) / pbs.box_count) END AS quantity FROM partial_warehouse_detail pwd LEFT JOIN v_partial ON pwd.partial_id = v_partial.partial_id JOIN partial_bussiness_standard pbs ON pbs.spec_kind = v_partial.spec_kind
AND `key` = #{key}
GROUP BY v_partial.spec_kind
