mapper.update:

UPDATE Material SET 



		qa_check_time = current_timestamp




		outline_time = coalesce(outline_time, current_timestamp)




		, bound_out_ocm = #{bound_out_ocm}




		, ocm_shipping_date = current_date



WHERE material_id = #{material_id}
-- AND outline_time IS NULL
;


UPDATE Material SET 
		qa_check_time = null
WHERE material_id = #{material_id};
