mapper.update:
UPDATE Material SET ocm = #{ocm}, level = #{level}, fix_type = #{fix_type}, agreed_date = #{agreed_date}, scheduled_date = CASE WHEN scheduled_expedited = 0 THEN #{scheduled_date} ELSE scheduled_date END, customer_id = #{customer_id}, ocm_rank = #{ocm_rank}, ocm_deliver_date = #{ocm_deliver_date}, osh_deliver_date = #{osh_deliver_date}, bound_out_ocm = #{bound_out_ocm}, area = #{area},
wip_location = #{wip_location}, wip_date = current_date,
selectable = #{selectable} WHERE material_id = #{material_id};
