mapper.select:
select p.process_code,p.line_id from material m JOIN process_assign pa ON m.pat_id = pa.refer_id AND pa.refer_type = 1 JOIN `position` p ON pa.position_id = p.position_id
WHERE material_id = #{material_id} AND NOT EXISTS (SELECT 1 FROM production_feature WHERE material_id = m.material_id AND position_id = p.position_id AND operate_result = 2)
and p.line_id = #{line_id}
