mapper.select:
SELECT CAST(`p`.POSITION_ID as char) as POSITION_ID, `p`.NAME, `p`.PROCESS_CODE, '0' as LIGHT_DIVISION_FLG, cast(coalesce(`pf`.material_count, 0) as char) as material_count, cast(coalesce(`pf2`.material_count, 0) as char) as light_fix_count, qf.quotation_first FROM `position` p JOIN (Select 0 as quotation_first from dual union Select 1 as quotation_first from dual) qf LEFT JOIN (SELECT count(p.material_id) as material_count, p.position_id, m.quotation_first FROM production_feature p join material m ON m.material_id = p.material_id WHERE p.finish_time IS NULL AND p.operate_result not in (5, 7) AND p.SECTION_ID = #{section_id} and m.level in (9 , 91, 92, 93, 96, 97, 98, 99) GROUP BY p.position_id, m.quotation_first) pf2 ON `p`.position_id = pf2.position_id AND qf.quotation_first = pf2.quotation_first LEFT JOIN (SELECT count(p.material_id) as material_count, p.position_id, m.quotation_first FROM production_feature p join material m ON m.material_id = p.material_id WHERE p.finish_time IS NULL AND p.operate_result not in (5, 7) AND p.SECTION_ID = #{section_id} and (m.level not in (9 , 91, 92, 93, 96, 97, 98, 99) or m.level is null) AND (p.position_id != 77 or (action_time is not null and finish_time is null)) GROUP BY p.position_id, m.quotation_first) pf ON `p`.position_id = pf.position_id AND qf.quotation_first = pf.quotation_first
JOIN position_of_section pos ON p.position_id = pos.position_id AND pos.SECTION_ID = #{section_id}
WHERE DELETE_FLG = 0
AND LINE_ID = #{line_id}
AND PROCESS_CODE like '4%'
-- [AND p.process_code <> '211']
ORDER BY `p`.PROCESS_CODE, quotation_first;

SELECT `m`.agreed_date, (`m`.scheduled_expedited * 10 + `mp`.line_expedited) as expedited, `m`.omr_notifi_no as sorc_no, `mb1`.order_date as partical_order_date, exists(SELECT 1 FROM material_partial mb2 WHERE `mb2`.material_id = `m`.material_id AND bo_flg = 1) as partical_bo, `mb3`.arrival_plan_date, '' as symbol, `m`.material_id, `m`.`level`, `m`.serial_no, `c`.name as category_name, `mdl`.name as model_name, `mp`.scheduled_date , `ppf1`.operate_result, `ppf1`.process_code, `ppf1`.position_id ,(select process_code from position where position_id =(select position_id from production_feature where material_id = m.material_id and position_id not in (select position_id from position where line_id = #{line_id}) and operate_result not in (5, 8) order by (1-ISNULL(action_time)), action_time desc, operate_result desc limit 1)) as otherline_process_code, (`mp`.`rework_trigger_position_id` IS NOT NULL) as is_reworking, (`mp`.`scheduled_assign_date` = current_date) as is_today FROM material m JOIN material_process mp ON `m`.material_id = `mp`.material_id AND `mp`.line_id = #{line_id} LEFT JOIN material_partial mb1 ON `m`.material_id = `mb1`.material_id AND `mb1`.occur_times = 1 LEFT JOIN material_partial mb3 ON `m`.material_id = `mb3`.material_id AND `mb3`.occur_times = 1 JOIN model mdl ON `m`.model_id = `mdl`.model_id JOIN category c ON `mdl`.category_id = `c`.category_id JOIN (SELECT `pfp`.material_id, `pfp`.position_id, `pfp`.process_code, `pfp`.operate_result FROM ( SELECT `pf`.material_id, `pf`.position_id, `p`.process_code, `pf`.operate_result FROM production_feature pf JOIN `position` p ON `pf`.position_id = `p`.position_id AND `p`.line_id = #{line_id} WHERE operate_result not in (5, 8) AND (`pf`.operate_result = 3 OR `pf`.finish_time is null) AND `pf`.section_id = #{section_id} ORDER BY (1-ISNULL(`pf`.action_time)) , `pf`.action_time DESC, (`pf`.operate_result = 3) DESC, `pf`.operate_result DESC ) pfp GROUP BY `pfp`.material_id) ppf1 ON `m`.material_id = `ppf1`.material_id WHERE `m`.section_id = #{section_id} AND `m`.outline_time IS NULL -- [AND `m`.fix_type = 1] AND (`mp`.`finish_date` IS NULL OR (`mp`.`rework_trigger_position_id` IS NOT NULL AND `ppf1`.operate_result != 2))
AND (ppf1.position_id = #{position_id} OR EXISTS (SELECT operate_result, process_code, position_id FROM production_feature WHERE material_id = `m`.material_id AND position_id = #{position_id} AND finish_time IS NULL))
ORDER BY `m`.agreed_date, `ppf1`.PROCESS_CODE;

SELECT count(distinct m.material_id) FROM material m JOIN material_process mp ON `m`.material_id = `mp`.material_id
AND `mp`.line_id = #{line_id}
JOIN `production_feature` pf ON `m`.material_id = `pf`.material_id AND operate_result not in (5, 8) AND pf.position_id not in (25, 60) JOIN `position` p ON pf.position_id = p.position_id WHERE `m`.section_id = #{section_id} AND `m`.outline_time IS NULL AND (`mp`.`finish_date` IS NULL OR (`mp`.`rework_trigger_position_id` IS NOT NULL and operate_result != 2))
AND p.`line_id` = #{line_id}
AND m.model_id not in (select model_id from v_model where kind = '03')
;


SELECT COUNT(1)
FROM material m
JOIN material_process mp
  ON `m`.material_id = `mp`.material_id AND `mp`.line_id = #{line_id}
JOIN v_model vm
  ON m.model_id = vm.model_id
WHERE `m`.section_id = #{section_id}
AND `mp`.scheduled_assign_date = current_date



AND vm.kind = #{kind}


SELECT COUNT(1)
FROM material m
JOIN material_process mp
  ON `m`.material_id = `mp`.material_id AND `mp`.line_id = #{line_id}
JOIN v_model vm
  ON m.model_id = vm.model_id
WHERE `m`.section_id = #{section_id}
AND `mp`.finish_date = current_date



AND vm.kind = #{kind}




    AND m.model_id not in (select model_id from v_model where kind = '03')
