mapper.select:
SELECT SUM(pwd.quantity * pbs.collect_case) FROM partial_warehouse_detail pwd JOIN v_partial ON v_partial.partial_id = pwd.partial_id JOIN partial_bussiness_standard pbs ON pbs.spec_kind = v_partial.spec_kind JOIN fact_production_feature fpf ON fpf.partial_warehouse_key = pwd.key WHERE
pwd.key = #{key}
fpf.operator_id = #{operator_id} AND fpf.production_type = 10 AND fpf.action_time >= CURRENT_DATE AND fpf.finish_time IS NOT NULL

SELECT CEIL(SUM(TIMESTAMPDIFF(SECOND,action_time, CASE WHEN finish_time IS NOT NULL THEN finish_time ELSE NOW() END)) / 60 ) FROM fact_production_feature WHERE operator_id = #{operator_id} AND production_type = #{production_type}
AND material_id = #{material_id}
AND partial_warehouse_key = #{key}
AND action_time >= CURRENT_DATE AND finish_time IS NOT NULL
AND finish_time IS NULL
